			ObtenirInfoPuntInput obtenirInfoPuntInput = getObtenirInfoPuntInputETRS89();
			InfoAdministrativa response = client.obtenirInfoPunt(obtenirInfoPuntInput);
			Assert.assertTrue(response != null);
		} catch (IccModuleException e) {
